import React, { useState, useEffect } from "react";
import "./app.scss";
import "./compononts/Result/result.scss";
import Header from "./compononts/header";
import Footer from "./compononts/Footer";
import Form from "./compononts/Form";
import Results from "./compononts/Result";
import axios from "axios";

export default function App() {
  let [data, setData] = useState(loading);
  let [count, setCount] = useState(0);
  let [requestParams, setrequestParams] = useState({});
  let [header, setHeader] = useState({});

  const callApi = async (requestParams, requestBody) => {
    let respond = await axios.get(requestParams.url);
    setData(respond.data);
    setrequestParams(requestParams);
    setCount(respond.data.count);
    setHeader({
      root: requestParams.url,
      cache_control: "string public",
      age: "86400",
      s_maxage: "86400",
    });

    console.log(requestBody);

    // for create newRecord:
    if (requestBody) {
      let reqBody = requestBody;
      let createRecord = await axios.post(requestParams.url, reqBody);
      setData(createRecord.data);
      console.log(createRecord.data);
    }
    // for update the record:
    if (requestBody) {
      let reqBody = requestBody;
      let createRecord = await axios.put(requestParams.url, reqBody);
      setData(createRecord.data);
      console.log(createRecord.data);
    }

    // console.log(respond.data);
  };

  return (
    <React.Fragment>
      <Header />
      <div className="div1" data-testid="url">
        {" "}
        {requestParams.method} {requestParams.url}
      </div>

      <Form handleApiCall={callApi} />
      <Results data={data} count={count} header={header} />
      <Footer />
    </React.Fragment>
  );
}
