import React, { useState } from "react";
import "./form.scss";

export default function Form({ handleApiCall }) {
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = {
      method: "GET",
      url: "https://pokeapi.co/api/v2/pokemon",
    };
    handleApiCall(formData);

    // callBack functions for state:

    const handleGetData = (e) => {
      e.preventDefault();
      setGetValue(e.target.name.value);
      console.log(getValue);
    };
  };

  // state variables for methods
  const [getValue, setGetValue] = useState("hello");

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          <span>URL: </span>
          <input name="url" type="text" />
          <button type="submit">GO!</button>
        </label>
        <label className="methods">
          <button id="get" onClick={handleGetData}>
            GET
          </button>
          <button id="post">POST</button>
          <button id="put">PUT</button>
          <button id="delete">DELETE</button>
        </label>
      </form>
    </>
  );
}
