import React from "react";

import "./app.scss";
import "./compononts/Result/result.scss";

// Let's talk about using index.js and some other name in the component folder
// There's pros and cons for each way of doing this ...
import Header from "./compononts/header";
import Footer from "./compononts/Footer";
import Form from "./compononts/Form";
import Results from "./compononts/Result";
import axios from "axios";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null,
      requestParams: {},
    };
  }

  callApi = async (requestParams, updateBody) => {


    const respond= await axios.get(this.state.requestParams.method)
    console.log(respond.data);
    // mock output
    const data = {
      count: 2,
      Header: [
        {
          root: "fakeData",
          cache_control: "string public",
          age: "86400",
          s_maxage: "86400",
        },
      ],
      Results: [
        { name: "fake thing 1", url: "http://fakethings.com/1" },
        { name: "fake thing 2", url: "http://fakethings.com/2" },
      ],
    };


    this.setState({ data, requestParams });

    const update = {
      count: 2,
      Header: [
        {
          root: "fakeData",
          cache_control: "string public",
          age: "86400",
          s_maxage: "86400",
        },
      ],
      Results: [{ body: updateBody }],
    };
    if (updateBody) {
      this.setState({ data: update });
    }
  };

  render() {
    return (
      <React.Fragment>
        <Header />
        <div className="div1" data-testid="url">
          {" "}
          {this.state.requestParams.method} {this.state.requestParams.url}
        </div>

        <Form handleApiCall={this.callApi} />
        <Results data={this.state.data} />
        <Footer />
      </React.Fragment>
    );
  }
}

export default App;
